// src/ProductReview.js
import { useState } from "react";
import "./Return_Refund.css";
const Return_Refund = () => {
  const [reviews, setReviews] = useState([]);
  const [name, setName] = useState("");
  const [review, setReview] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (name.trim() && review.trim()) {
      setReviews([...reviews, { name, review }]);
      setName("");
      setReview("");
    }
  };

  return (
    <div className="max-w-lg mx-auto p-4 bg-white shadow-lg rounded-lg">
      <h2 className="text-xl font-semibold mb-4">Product Reviews</h2>
      <form onSubmit={handleSubmit} className="mb-4">
        <input
          type="text"
          placeholder="Your Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="w-full p-2 border rounded mb-2"
          required
        />
        <textarea
          placeholder="Write your review..."
          value={review}
          onChange={(e) => setReview(e.target.value)}
          className="w-full p-2 border rounded mb-2"
          required
        ></textarea>
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 w-full"
        >
          Submit Review
        </button>
      </form>
      <div>
        {reviews.length > 0 ? (
          reviews.map((rev, index) => (
            <div key={index} className="p-3 border rounded mb-2">
              <h4 className="font-semibold">{rev.name}</h4>
              <p>{rev.review}</p>
            </div>
          ))
        ) : (
          <p>No reviews yet. Be the first to review!</p>
        )}
      </div>
    </div>
  );
};

export default Return_Refund;
