import { useState } from "react";

const Return_Refund = () => {
  const [formData, setFormData] = useState({
    orderId: "",
    reason: "",
    refundMethod: "Bank Transfer",
  });
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setError(""); // Clear errors on input change
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.orderId || !formData.reason) {
      setError("All fields are required.");
      return;
    }
    setSuccessMessage("Your return request has been submitted successfully!");
    setFormData({ orderId: "", reason: "", refundMethod: "Bank Transfer" });
  };

  return (
    <div className="p-4 bg-gray-100 min-h-screen flex items-center justify-center">
      <div className="max-w-md mx-auto bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-semibold mb-4">Return & Refund Request</h2>
        {error && <p className="text-red-500 text-sm mb-2">{error}</p>}
        {successMessage && (
          <p className="text-green-500 text-sm mb-2">{successMessage}</p>
        )}

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-gray-700">Order ID:</label>
            <input
              type="text"
              name="orderId"
              value={formData.orderId}
              onChange={handleChange}
              className="w-full border rounded-md p-2"
              placeholder="Enter Order ID"
              required
            />
          </div>

          <div>
            <label className="block text-gray-700">Reason for Return:</label>
            <textarea
              name="reason"
              value={formData.reason}
              onChange={handleChange}
              className="w-full border rounded-md p-2"
              placeholder="Describe the issue"
              required
            />
          </div>

          <div>
            <label className="block text-gray-700">Refund Method:</label>
            <select
              name="refundMethod"
              value={formData.refundMethod}
              onChange={handleChange}
              className="w-full border rounded-md p-2"
            >
              <option>Bank Transfer</option>
              <option>Store Credit</option>
              <option>Original Payment Method</option>
            </select>
          </div>

          <button
            type="submit"
            className="w-full bg-blue-500 text-white py-2 rounded-md hover:bg-blue-600 transition"
          >
            Submit Request
          </button>
        </form>
      </div>
    </div>
  );
};

export default Return_Refund;
