import React, { useRef, useEffect } from "react";
import "./DragableCard.css";
import laptop1 from "../../User-images/laptop35.webp";
import laptop2 from "../../User-images/laptop45.webp";
import laptop3 from "../../User-images/laptop31.webp";
import laptop4 from "../../User-images/laptop23.webp";
import laptop5 from "../../User-images/laptop25.webp";
import laptop6 from "../../User-images/laptop29.webp";
import laptop7 from "../../User-images/laptop27.webp";

const ScrollImages = () => {
  const scrollContainerRef = useRef(null);

  const leftScroll = () => {
    const scrollContainer = scrollContainerRef.current;
    scrollContainer.scrollBy({
      left: -scrollContainer.offsetWidth / 1, // Adjust scroll step for smooth movement
      behavior: "smooth",
    });
  };

  const rightScroll = () => {
    const scrollContainer = scrollContainerRef.current;
    scrollContainer.scrollBy({
      left: scrollContainer.offsetWidth / 1, // Adjust scroll step for smooth movement
      behavior: "smooth",
    });
  };

  const autoScroll = () => {
    const scrollContainer = scrollContainerRef.current;
    const maxScrollLeft =
      scrollContainer.scrollWidth - scrollContainer.offsetWidth;

    if (scrollContainer.scrollLeft >= maxScrollLeft) {
      scrollContainer.scrollTo({
        left: 0,
        behavior: "smooth",
      });
    } else {
      scrollContainer.scrollBy({
        left: scrollContainer.offsetWidth / 1, // Adjust scroll step for smooth movement
        behavior: "smooth",
      });
    }
  };

  useEffect(() => {
    const interval = setInterval(autoScroll, 2500);
    return () => clearInterval(interval); // Clean up interval on component unmount
  }, []);

  return (
    <div>
      <div>
        <br />
        <h5
          style={{
            paddingLeft: "5%",
            fontFamily: "inherit",
            color: "black",
          }}
        >
          <b>Latest Laptop Top Picks :</b>
        </h5>
        <hr
          style={{
            marginLeft: "5%",
            marginRight: "5%",
          }}
        />
      </div>
      <div className="cover">
        <button className="left" onClick={leftScroll}>
          ←
        </button>
        <div className="scroll-images" ref={scrollContainerRef}>
          <div className="child">
            <a href="laptop1.html">
              <img className="child-img" src={laptop1} alt="laptop_image" />
            </a>
          </div>
          <div className="child">
            <a href="laptop2.html">
              <img className="child-img" src={laptop2} alt="laptop_image" />
            </a>
          </div>
          <div className="child">
            <a href="laptop3.html">
              <img className="child-img" src={laptop3} alt="laptop_image" />
            </a>
          </div>
          <div className="child">
            <a href="laptop4.html">
              <img className="child-img" src={laptop4} alt="laptop_image" />
            </a>
          </div>
          <div className="child">
            <a href="laptop5.html">
              <img className="child-img" src={laptop5} alt="laptop_image" />
            </a>
          </div>
          <div className="child">
            <a href="laptop6.html">
              <img className="child-img" src={laptop6} alt="laptop_image" />
            </a>
          </div>
          <div className="child">
            <a href="laptop7.html">
              <img className="child-img" src={laptop7} alt="laptop_image" />
            </a>
          </div>
          <div className="child">
            <a href="laptop1.html">
              <img className="child-img" src={laptop1} alt="laptop_image" />
            </a>
          </div>
          <div className="child">
            <a href="laptop1.html">
              <img className="child-img" src={laptop1} alt="laptop_image" />
            </a>
          </div>
          <div className="child">
            <a href="laptop1.html">
              <img className="child-img" src={laptop1} alt="laptop_image" />
            </a>
          </div>
          <div className="child">
            <a href="laptop1.html">
              <img className="child-img" src={laptop1} alt="laptop_image" />
            </a>
          </div>
          <div className="child">
            <a href="laptop1.html">
              <img className="child-img" src={laptop1} alt="laptop_image" />
            </a>
          </div>
          <div className="child">
            <a href="laptop1.html">
              <img className="child-img" src={laptop1} alt="laptop_image" />
            </a>
          </div>
          <div className="child">
            <a href="laptop1.html">
              <img className="child-img" src={laptop1} alt="laptop_image" />
            </a>
          </div>
        </div>
        <button className="right" onClick={rightScroll}>
          →
        </button>
      </div>
    </div>
  );
};

export default ScrollImages;
