import React, { useEffect, useRef, useState } from "react";
import "./DragableCard.css";

function DragableCard() {
  const carouselRef = useRef(null);
  const wrapperRef = useRef(null);
  const [timeoutId, setTimeoutId] = useState(null);

  useEffect(() => {
    const carousel = carouselRef.current;
    const wrapper = wrapperRef.current;
    const arrowBtns = wrapper.querySelectorAll("i");

    const firstCard = carousel.querySelector(".card");
    const firstCardWidth = firstCard ? firstCard.offsetWidth : 0;

    let isDragging = false;
    let startX, startScrollLeft;

    const dragStart = (e) => {
      isDragging = true;
      carousel.classList.add("dragging");
      startX = e.pageX;
      startScrollLeft = carousel.scrollLeft;
    };

    const dragging = (e) => {
      if (!isDragging) return;

      // Calculate the new scroll position
      const newScrollLeft = startScrollLeft - (e.pageX - startX);

      // Check if the new scroll position exceeds carousel boundaries
      if (
        newScrollLeft <= 0 ||
        newScrollLeft >= carousel.scrollWidth - carousel.offsetWidth
      ) {
        isDragging = false;
        return;
      }

      // Otherwise, update the scroll position of the carousel
      carousel.scrollLeft = newScrollLeft;
    };

    const dragStop = () => {
      isDragging = false;
      carousel.classList.remove("dragging");
    };

    const autoPlay = () => {
      // Return if window is smaller than 800
      if (window.innerWidth < 800) return;

      // Calculate the total width of all cards
      const totalCardWidth = carousel.scrollWidth;

      // Calculate the maximum scroll position
      const maxScrollLeft = totalCardWidth - carousel.offsetWidth;

      // If the carousel is at the end, stop autoplay
      if (carousel.scrollLeft >= maxScrollLeft) return;

      // Autoplay the carousel after every 2500ms
      setTimeoutId(
        setTimeout(() => (carousel.scrollLeft += firstCardWidth), 2500)
      );
    };

    // Add event listeners for mouse events
    carousel.addEventListener("mousedown", dragStart);
    carousel.addEventListener("mousemove", dragging);
    document.addEventListener("mouseup", dragStop);
    wrapper.addEventListener("mouseenter", () => clearTimeout(timeoutId));
    wrapper.addEventListener("mouseleave", autoPlay);

    // Add event listeners for the arrow buttons to scroll the carousel left and right
    arrowBtns.forEach((btn) => {
      btn.addEventListener("click", () => {
        carousel.scrollLeft +=
          btn.id === "left" ? -firstCardWidth : firstCardWidth;
      });
    });

    // Clean up event listeners when the component is unmounted
    return () => {
      carousel.removeEventListener("mousedown", dragStart);
      carousel.removeEventListener("mousemove", dragging);
      document.removeEventListener("mouseup", dragStop);
      wrapper.removeEventListener("mouseenter", () => clearTimeout(timeoutId));
      wrapper.removeEventListener("mouseleave", autoPlay);
      arrowBtns.forEach((btn) => {
        btn.removeEventListener("click", () => {
          carousel.scrollLeft +=
            btn.id === "left" ? -firstCardWidth : firstCardWidth;
        });
      });
    };
  }, [timeoutId]);

  return (
    <div>
      <br />
      <h5 style={{ paddingLeft: "5%", fontFamily: "inherit", color: "black" }}>
        <b>Latest Laptop Top Picks :</b>
      </h5>
      <hr style={{ marginLeft: "5%", marginRight: "5%" }} />
      <div className="wrapper" ref={wrapperRef}>
        <i id="left" className="fa-solid fas fa-angle-left"></i>
        <ul className="carousel" ref={carouselRef}>
          {/* Add your carousel cards */}
          <li className="card">
            <div className="img">
              <img src="your-image-src" alt="Card 1" draggable="false" />
            </div>
            <h2 style={{ color: "green", fontWeight: "bold" }}>
              GeeksforGeeks
            </h2>
            <span>Coding Platform</span>
          </li>
          {/* Add more card items */}
        </ul>
        <i id="right" className="fa-solid fas fa-angle-right"></i>
      </div>
    </div>
  );
}

export default DragableCard;
