import React from "react";
import "./SignUp.css"; // Import the CSS file
import { Routes, Route, Link, useNavigate } from "react-router-dom"; // This is used to enable routing.

function SignUp() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    password: "",
    confirmPassword: "",
  });

  const navigate = useNavigate(); // Get the navigate function

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/user/signup", {
        method: "POST",
        credentials: "include",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      const data = await res.json();
      // console.log("data", data);

      if (res.ok) {
        alert("Signup Successful!");
        //clear the form data after the signup
        setFormData({ name: "", email: "", password: "" });
        navigate("/chatApp"); // after the signup navigate to chatApp page(This is used for the navigation)
      } else {
        alert(data.message || "Signup failed. Try again.");
      }
    } catch (error) {
      console.error("Error:", error);
      alert("Something went wrong. Please try again.");
    }
  };
  return (
    <div>
      <br />
      <br />
      <br />
      <div className="signup-container">
        <h2>SignUp</h2>
        <form action="#">
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            placeholder="Enter your username"
            required
          />
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="Enter your email"
            required
          />
          <input
            type="text"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            placeholder="Enter your number"
            required
          />
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            placeholder="Enter your password"
            required
          />
          <input
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
            placeholder="Confirm your password"
            required
          />

          <div className="login-link">
            <Link to="/login">Already have an account? Login</Link>
          </div>

          <button type="submit">Register</button>
        </form>
      </div>
      <br />
      <br />
      <br />
    </div>
  );
}

export default SignUp;
