import { useState } from "react";
import "./Return_Refund.css";

const ProductReview = () => {
  const [reviews, setReviews] = useState([]);
  const [name, setName] = useState("");
  const [review, setReview] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (name.trim() && review.trim()) {
      setReviews([...reviews, { name, review }]);
      setName("");
      setReview("");
    }
  };

  return (
    <div className="product-review-container">
      <h2 className="product-review-title">Product Reviews</h2>
      <form onSubmit={handleSubmit} className="product-review-form">
        <input
          type="text"
          placeholder="Your Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <textarea
          placeholder="Write your review..."
          value={review}
          onChange={(e) => setReview(e.target.value)}
          required
        ></textarea>
        <button type="submit" className="product-review-button">
          Submit Review
        </button>
      </form>
      <div className="product-review-list">
        {reviews.length > 0 ? (
          reviews.map((rev, index) => (
            <div key={index} className="product-review-item">
              <h4>{rev.name}</h4>
              <p>{rev.review}</p>
            </div>
          ))
        ) : (
          <p>No reviews yet. Be the first to review!</p>
        )}
      </div>
    </div>
  );
};

export default ProductReview;
